#import "ImageWithDimensions.h"
@implementation ImageWithDimensions

+(void) AddImage: (UIImage *)image withDimensons: (NSValue *)value{
  if(!imagesAndDimensions){
    imagesAndDimensions = [NSMutableDictionary new];
  }
  NSValue *myKey = [NSValue valueWithNonretainedObject:image];
  [imagesAndDimensions setObject:value forKey:myKey];
}

+(NSValue *) GetImageDimensions: (UIImage *)image{
  if(!imagesAndDimensions){
    imagesAndDimensions = [NSMutableDictionary new];
  }
  NSValue *myKey = [NSValue valueWithNonretainedObject:image];
  return [imagesAndDimensions objectForKey:myKey];
}

@end
